const annualAudienceReachValue = inputs.annualAttendance * inputs.averageTicketPrice * 0.005; 
const mediaExposureValue = (inputs.annualMediaImpressions / 1000000) * 10000; 
const demographicBoost = 1 + (inputs.demographicPremiumScore - 1) * 0.05; 
const venueQualityMultiplier = inputs.venuePrestigeFactor * inputs.communityImpactMultiplier; 

let baseAnnualValue = (annualAudienceReachValue + mediaExposureValue); 
baseAnnualValue *= demographicBoost; 
baseAnnualValue *= venueQualityMultiplier; 

baseAnnualValue = Math.max(baseAnnualValue, 50000); 

const maxPossibleAnnual = 5000000; 
baseAnnualValue = Math.min(baseAnnualValue, maxPossibleAnnual); 

const totalNamingRightsValue = baseAnnualValue * inputs.contractLengthYears; 
const annualNamingRightsValue = totalNamingRightsValue / inputs.contractLengthYears; 

return { 
    totalNamingRightsValue: totalNamingRightsValue, 
    annualNamingRightsValue: annualNamingRightsValue 
};
