const visaTypeLower = inputs.visaType.toLowerCase();

let baseGovFeePerApplicant = 0;
let baseProcessingMonths = 0;
let legalComplexityMultiplier = 1.0;

switch (visaTypeLower) {
    case 'h1b':
        baseGovFeePerApplicant = 5500; // Includes filing fee, fraud fee, ACWIA fee (for large employers), public law fee
        baseProcessingMonths = 8;
        legalComplexityMultiplier = 1.1;
        break;
    case 'f1':
        baseGovFeePerApplicant = 350; // SEVIS fee + application fee
        baseProcessingMonths = 3;
        legalComplexityMultiplier = 0.5;
        break;
    case 'eb-2':
        baseGovFeePerApplicant = 8000; // Includes I-140, I-485, biometrics, PERM related if applicable
        baseProcessingMonths = 18;
        legalComplexityMultiplier = 1.6;
        break;
    case 'family green card':
    case 'family-based green card':
        baseGovFeePerApplicant = 4500; // Includes I-130, I-485/DS-260, biometrics, medical exam estimate
        baseProcessingMonths = 12;
        legalComplexityMultiplier = 1.3;
        break;
    case 'l1':
        baseGovFeePerApplicant = 6000; // Includes I-129, fraud fee, ACWIA (if applicable)
        baseProcessingMonths = 10;
        legalComplexityMultiplier = 1.2;
        break;
    default:
        baseGovFeePerApplicant = 3000; // General placeholder for other types
        baseProcessingMonths = 10;
        legalComplexityMultiplier = 0.9;
}

const initialLegalCounselBaseFee = 3500; // Base attorney fee for the primary petition
const perDependentLegalFee = 800; // Additional attorney fee per dependent
const expeditedServiceBaseFee = 2500; // USCIS Premium Processing fee (if available for visa type)
const expeditedServicePerDependentAddon = 400; // Additional legal prep for dependents for expedited services
const expeditedTimeReductionMonths = 5; // Max time reduction for expedited services
const minProcessingTimeMonths = 2; // Minimum possible processing time

let totalGovFees = baseGovFeePerApplicant * inputs.numApplicants;

let legalFee = 0;
if (inputs.hasLegalCounsel === 1) {
    const incomeImpactMultiplier = 1 + Math.min(0.7, inputs.sponsorIncome / 150000); // Scales up to 70% increase for income >= 150k
    legalFee = initialLegalCounselBaseFee * legalComplexityMultiplier * incomeImpactMultiplier;
    if (inputs.numApplicants > 1) {
        legalFee += (inputs.numApplicants - 1) * perDependentLegalFee;
    }
}

let expeditedFee = 0;
let timeReduction = 0;
if (inputs.isExpedited === 1) {
    expeditedFee = expeditedServiceBaseFee;
    if (inputs.numApplicants > 1) {
        expeditedFee += (inputs.numApplicants - 1) * expeditedServicePerDependentAddon;
    }
    timeReduction = expeditedTimeReductionMonths;
}

const totalCost = totalGovFees + legalFee + expeditedFee;
let estimatedTime = Math.max(minProcessingTimeMonths, baseProcessingMonths - timeReduction);

// Add a small buffer for highly complex cases based on legal complexity and income
if (inputs.hasLegalCounsel === 1 && legalComplexityMultiplier > 1.2 && inputs.sponsorIncome > 100000) {
    estimatedTime += 0.5;
}

return {
    estimatedTime: estimatedTime,
    totalCost: totalCost
};
