const totalProgramCost = tuitionCost + (lostIncomePerMonth * programDurationMonths) + otherProgramExpenses;
const annualSalaryIncrease = postTrainingAnnualSalary - preTrainingAnnualSalary;
const totalFinancialBenefit = annualSalaryIncrease * careerLifespanYears;
const netFinancialGain = totalFinancialBenefit - totalProgramCost;

const returnOnInvestment = totalProgramCost > 0 ? (netFinancialGain / totalProgramCost) * 100 : 0;

let paybackPeriodYears = 'N/A';
if (annualSalaryIncrease > 0) {
  paybackPeriodYears = totalProgramCost / annualSalaryIncrease;
  // If totalProgramCost is negative (e.g., due to grants exceeding costs), payback is effectively immediate
  paybackPeriodYears = paybackPeriodYears < 0 ? 0 : paybackPeriodYears;
} else if (annualSalaryIncrease <= 0 && totalProgramCost > 0) {
  paybackPeriodYears = 'Never'; // Cannot pay back if no increase or decrease, and there's a cost
}

return {
  totalProgramCost: totalProgramCost,
  totalFinancialBenefit: totalFinancialBenefit,
  netFinancialGain: netFinancialGain,
  returnOnInvestment: returnOnInvestment,
  paybackPeriodYears: paybackPeriodYears
};
