function calculateROI(inputs) {
  const initialCurrentSalary = inputs.initialCurrentSalary || 0;
  const postTrainingSalaryExpected = inputs.postTrainingSalaryExpected || 0;
  const trainingProgramCost = inputs.trainingProgramCost || 0;
  const lostIncomeDuringTraining = inputs.lostIncomeDuringTraining || 0;
  const timeHorizonYears = inputs.timeHorizonYears || 1;
  const annualSalaryGrowthRateCurrent = (inputs.annualSalaryGrowthRateCurrent || 0) / 100;
  const annualSalaryGrowthRateIndustry = (inputs.annualSalaryGrowthRateIndustry || 0) / 100;
  const taxRate = (inputs.taxRate || 0) / 100;

  // Ensure minimum values for calculations
  if (timeHorizonYears <= 0) return {
    totalInvestmentCost: 0, annualNetSalaryIncreaseInitial: 0, paybackPeriodMonths: 'N/A',
    netFinancialGain: 0, totalRoiPercentage: 0
  };

  // 1. Calculate Total Initial Investment Cost
  const totalInvestmentCost = trainingProgramCost + lostIncomeDuringTraining;

  // 2. Calculate Initial Annual Net Salary Increase (Post-Tax)
  const currentNetSalaryInitial = initialCurrentSalary * (1 - taxRate);
  const postTrainingNetSalaryInitial = postTrainingSalaryExpected * (1 - taxRate);
  const annualNetSalaryIncreaseInitial = postTrainingNetSalaryInitial - currentNetSalaryInitial;

  // 3. Project Cumulative Net Gain over the Time Horizon
  let cumulativeIncrementalNetGain = 0;
  for (let i = 0; i < timeHorizonYears; i++) {
    const projectedCurrentGrossSalary = initialCurrentSalary * Math.pow(1 + annualSalaryGrowthRateCurrent, i);
    const projectedPostTrainingGrossSalary = postTrainingSalaryExpected * Math.pow(1 + annualSalaryGrowthRateIndustry, i);

    const projectedCurrentNetSalary = projectedCurrentGrossSalary * (1 - taxRate);
    const projectedPostTrainingNetSalary = projectedPostTrainingGrossSalary * (1 - taxRate);

    cumulativeIncrementalNetGain += (projectedPostTrainingNetSalary - projectedCurrentNetSalary);
  }

  // 4. Calculate Total Net Financial Gain over Time Horizon
  const netFinancialGain = cumulativeIncrementalNetGain - totalInvestmentCost;

  // 5. Calculate Total ROI Percentage
  let totalRoiPercentage;
  if (totalInvestmentCost === 0) {
    totalRoiPercentage = (netFinancialGain > 0) ? Infinity : (netFinancialGain === 0 ? 0 : -Infinity);
  } else {
    totalRoiPercentage = (netFinancialGain / totalInvestmentCost) * 100;
  }

  // 6. Calculate Payback Period
  let paybackPeriodMonths;
  if (annualNetSalaryIncreaseInitial <= 0) {
    paybackPeriodMonths = Infinity;
  } else {
    paybackPeriodMonths = (totalInvestmentCost / annualNetSalaryIncreaseInitial) * 12;
  }

  // Cap payback period if it's extremely long
  if (paybackPeriodMonths > 99999) paybackPeriodMonths = Infinity;

  return {
    totalInvestmentCost: totalInvestmentCost,
    annualNetSalaryIncreaseInitial: annualNetSalaryIncreaseInitial,
    paybackPeriodMonths: paybackPeriodMonths,
    netFinancialGain: netFinancialGain,
    totalRoiPercentage: totalRoiPercentage
  };
}
