const { conductorMaterial, awgGauge, conductorLength, loadCurrent, systemVoltage, operatingTemperature, systemPhases } = inputs;

  // Constants
  const RHO_COPPER_20C_OHMS_CMIL_FT = 10.37; // Copper resistivity at 20°C in Ohms per circular mil-foot
  const RHO_ALUMINUM_20C_OHMS_CMIL_FT = 17.02; // Aluminum resistivity at 20°C in Ohms per circular mil-foot
  const ALPHA_COPPER = 0.00393; // Temperature coefficient for Copper per °C
  const ALPHA_ALUMINUM = 0.00403; // Temperature coefficient for Aluminum per °C
  const REFERENCE_TEMP_C = 20; // Reference temperature for resistivity in °C

  // Circular Mils (CM) lookup table for common AWG gauges
  const awgToCM = {
    '18': 1620, '16': 2580, '14': 4110, '12': 6530, '10': 10380, '8': 16510,
    '6': 26240, '4': 41740, '2': 66360, '1': 83690, '0': 105600, '00': 133100,
    '000': 167800, '0000': 211600
  };

  // Input validation
  if (loadCurrent <= 0 || systemVoltage <= 0 || conductorLength <= 0 || awgGauge <= 0) {
    return { error: 'Current, Voltage, Length, and AWG must be positive values.' };
  }
  if (!awgToCM[awgGauge.toString()]) {
    return { error: 'Invalid AWG gauge. Please use a common gauge between 18 and 0000.' };
  }
  if (systemPhases !== 1 && systemPhases !== 3) {
    return { error: 'System Phases must be 1 (single-phase) or 3 (three-phase).' };
  }

  // Determine material properties
  let rho_ref, alpha;
  const material = conductorMaterial.toLowerCase().trim();
  if (material === 'copper') {
    rho_ref = RHO_COPPER_20C_OHMS_CMIL_FT;
    alpha = ALPHA_COPPER;
  } else if (material === 'aluminum') {
    rho_ref = RHO_ALUMINUM_20C_OHMS_CMIL_FT;
    alpha = ALPHA_ALUMINUM;
  } else {
    return { error: 'Invalid conductor material. Please specify "Copper" or "Aluminum".' };
  }

  // Calculate resistivity at operating temperature
  const rho_T = rho_ref * (1 + alpha * (operatingTemperature - REFERENCE_TEMP_C));

  // Get circular mils for the given AWG gauge
  const circularMils = awgToCM[awgGauge.toString()];

  // Calculate total conductor resistance (one way, in Ohms)
  const resistancePerConductor = (rho_T * conductorLength) / circularMils;

  let voltageDropFactor;
  if (systemPhases === 1) {
    voltageDropFactor = 2; // For single-phase, current travels round trip (out and back)
  } else { // systemPhases === 3
    voltageDropFactor = Math.sqrt(3); // For three-phase, line-to-line voltage drop
  }

  // Calculate voltage drop
  const voltageDrop = voltageDropFactor * loadCurrent * resistancePerConductor;

  // Calculate percentage drop
  const percentDrop = (voltageDrop / systemVoltage) * 100;

  return {
    voltageDrop,
    percentDrop,
    totalResistance: resistancePerConductor * voltageDropFactor // Total effective resistance for the circuit
  };
