const equityValue = inputs.equityValue;
const debtValue = inputs.debtValue;
const costOfEquity = inputs.costOfEquity / 100;
const costOfDebt = inputs.costOfDebt / 100;
const taxRate = inputs.taxRate / 100;

const totalCapital = equityValue + debtValue;

if (totalCapital === 0) {
  return { wacc: 0, totalCapital: 0 };
}

const weightOfEquity = equityValue / totalCapital;
const weightOfDebt = debtValue / totalCapital;

const wacc = (weightOfEquity * costOfEquity) + (weightOfDebt * costOfDebt * (1 - taxRate));

return {
  wacc: wacc * 100,
  totalCapital: totalCapital
};
