const totalFixtureUnits = inputs.totalFixtureUnits;
const systemPressurePSI = inputs.systemPressurePSI;
const pipeDiameterInches = inputs.pipeDiameterInches;
const fixtureUnitFlowFactorGPMperFU = inputs.fixtureUnitFlowFactorGPMperFU || 0.25; // Default to 0.25 GPM/FU if not provided
const waterHammerSeverityFactor = inputs.waterHammerSeverityFactor || 1.0; // Default to 1.0 if not provided

// Input validation and default fallback for safety
if (totalFixtureUnits <= 0 || systemPressurePSI <= 0 || pipeDiameterInches <= 0) {
    throw new Error("All primary inputs must be positive values.");
}

// 1. Calculate Estimated Peak Flow Rate (GPM)
let estimatedPeakFlowRateGPM = totalFixtureUnits * fixtureUnitFlowFactorGPMperFU;
// Cap peakFlowGPM to prevent absurdly high values affecting calculations, typical max for a single arrestor zone.
estimatedPeakFlowRateGPM = Math.min(estimatedPeakFlowRateGPM, 150); 

// 2. Calculate Recommended Arrestor Capacity (cu. in.)
// Baseline capacity factor based on PDI WH-201 general guidelines (simplified: cu. in. per GPM)
const BASE_CAPACITY_FACTOR_CUIN_PER_GPM = 12; 

// Pressure adjustment: Higher pressure requires more capacity to absorb the same energy surge.
// Assuming 60 PSI is a nominal pressure. Adjustment is linear above nominal.
const NOMINAL_PRESSURE_PSI = 60;
let pressureAdjustment = Math.max(1.0, systemPressurePSI / NOMINAL_PRESSURE_PSI);

// Pipe Diameter adjustment: Larger diameter implies potentially more water mass or volume needing protection for the same flow.
// Using a square root relationship to temper the effect of large diameter changes. Nominal diameter 0.75" (3/4").
const NOMINAL_PIPE_DIAMETER_INCHES = 0.75; 
let pipeDiameterAdjustment = Math.max(1.0, Math.pow(pipeDiameterInches / NOMINAL_PIPE_DIAMETER_INCHES, 0.5)); 

// Calculate the initial base capacity based on flow
let recommendedArrestorCapacityCUIN = estimatedPeakFlowRateGPM * BASE_CAPACITY_FACTOR_CUIN_PER_GPM;

// Apply adjustments based on system characteristics and severity factor
recommendedArrestorCapacityCUIN *= pressureAdjustment;
recommendedArrestorCapacityCUIN *= waterHammerSeverityFactor;
recommendedArrestorCapacityCUIN *= pipeDiameterAdjustment;

// Ensure recommendedArrestorCapacityCUIN has a minimum realistic value (e.g., equivalent to PDI 'A' size)
recommendedArrestorCapacityCUIN = Math.max(8, recommendedArrestorCapacityCUIN); 

// 3. Determine Suggested Arrestor Size Category (A, B, C, D, E, F, or F+)
let suggestedArrestorSizeCategory = "Unknown";
// Sizing categories based on general PDI WH-201 guidelines for cumulative volume capacity.
if (recommendedArrestorCapacityCUIN <= 10) {
    suggestedArrestorSizeCategory = "A"; 
} else if (recommendedArrestorCapacityCUIN <= 25) {
    suggestedArrestorSizeCategory = "B"; 
} else if (recommendedArrestorCapacityCUIN <= 50) {
    suggestedArrestorSizeCategory = "C"; 
} else if (recommendedArrestorCapacityCUIN <= 100) {
    suggestedArrestorSizeCategory = "D"; 
} else if (recommendedArrestorCapacityCUIN <= 200) {
    suggestedArrestorSizeCategory = "E"; 
} else if (recommendedArrestorCapacityCUIN <= 400) {
    suggestedArrestorSizeCategory = "F"; 
} else {
    suggestedArrestorSizeCategory = "F+ (Consult Engineer for Custom Solution)"; // For very large systems requiring multiple or specialized arrestors
}

return {
    estimatedPeakFlowRateGPM,
    recommendedArrestorCapacityCUIN,
    suggestedArrestorSizeCategory
};
