let kg_to_ml_base = 33; /* ml per kg */
let lbs_to_oz_base = 0.5; /* oz per lb */
let oz_to_ml = 29.5735; /* ml per oz */

let base_ml;
if (weight_unit === 'lbs') {
    base_ml = weight * lbs_to_oz_base * oz_to_ml;
} else { /* weight_unit === 'kg' */
    base_ml = weight * kg_to_ml_base;
}

let activity_factor = 1;
if (activity_level === 'sedentary') {
    activity_factor = 0.9; /* 10% less for sedentary */
} else if (activity_level === 'active') {
    activity_factor = 1.2; /* 20% more for active */
}

let climate_factor = 1;
if (climate === 'warm') {
    climate_factor = 1.1; /* 10% more for warm climate */
} else if (climate === 'very_hot') {
    climate_factor = 1.25; /* 25% more for very hot climate */
}

let result_ml = base_ml * activity_factor * climate_factor;
return result_ml;
