function calculate({
    initial_resilient_cost,
    initial_conventional_cost,
    project_lifespan,
    annual_maintenance_savings,
    annual_avoided_damage_cost,
    annual_water_reduction_volume,
    current_water_cost_per_m3,
    annual_water_cost_increase_rate,
    discount_rate,
    climate_impact_escalation_start_year,
    climate_impact_escalation_rate
}) {
    const initialInvestmentDifference = initial_resilient_cost - initial_conventional_cost;
    const discountRateDecimal = discount_rate / 100;
    const waterCostIncreaseRateDecimal = annual_water_cost_increase_rate / 100;
    const climateEscalationRateDecimal = climate_impact_escalation_rate / 100;

    let totalLifetimeBenefitsNPV = 0;
    let cumulativeNetCashFlowForPayback = -initialInvestmentDifference; // Start with initial investment outflow
    let paybackPeriodYears = null;
    let totalClimateAmplifiedBenefitsNPV = 0;

    for (let year = 1; year <= project_lifespan; year++) {
        // Calculate water cost for the current year
        const currentYearWaterCostPerM3 = current_water_cost_per_m3 * Math.pow(1 + waterCostIncreaseRateDecimal, year - 1);
        const annualWaterSavingsValue = annual_water_reduction_volume * currentYearWaterCostPerM3;

        let currentYearAvoidedDamage = annual_avoided_damage_cost;
        let currentYearWaterSavingsValue = annualWaterSavingsValue;

        let amplifiedAvoidedDamageThisYear = 0;
        let amplifiedWaterSavingsThisYear = 0;

        if (year >= climate_impact_escalation_start_year) {
            const yearsSinceEscalationStart = year - climate_impact_escalation_start_year + 1;
            const climateImpactMultiplier = Math.pow(1 + climateEscalationRateDecimal, yearsSinceEscalationStart);

            // Calculate the *additional* benefit due to climate amplification
            amplifiedAvoidedDamageThisYear = currentYearAvoidedDamage * (climateImpactMultiplier - 1);
            amplifiedWaterSavingsThisYear = currentYearWaterSavingsValue * (climateImpactMultiplier - 1);

            currentYearAvoidedDamage *= climateImpactMultiplier;
            currentYearWaterSavingsValue *= climateImpactMultiplier;
        }

        const totalAnnualBenefit = annual_maintenance_savings + currentYearAvoidedDamage + currentYearWaterSavingsValue;
        const discountFactor = 1 / Math.pow(1 + discountRateDecimal, year);
        const discountedAnnualBenefit = totalAnnualBenefit * discountFactor;

        totalLifetimeBenefitsNPV += discountedAnnualBenefit;
        cumulativeNetCashFlowForPayback += discountedAnnualBenefit;

        // Sum only the amplified portion for the dedicated output
        totalClimateAmplifiedBenefitsNPV += (amplifiedAvoidedDamageThisYear + amplifiedWaterSavingsThisYear) * discountFactor;

        if (paybackPeriodYears === null && cumulativeNetCashFlowForPayback >= 0) {
            paybackPeriodYears = year; // Simple payback period in years
            // For more precision, we could interpolate within the year:
            // paybackPeriodYears = year - 1 + (Math.abs(cumulativeNetCashFlowForPayback - discountedAnnualBenefit) / discountedAnnualBenefit);
        }
    }

    const netPresentValue = totalLifetimeBenefitsNPV - initialInvestmentDifference;

    // Handle edge case for payback period if not met within lifespan
    if (paybackPeriodYears === null) {
        paybackPeriodYears = Infinity; // Or a specific message like 'N/A'
    }

    return {
        initial_investment_difference: initialInvestmentDifference,
        total_lifetime_benefits_npv: totalLifetimeBenefitsNPV,
        net_present_value: netPresentValue,
        payback_period_years: paybackPeriodYears === Infinity ? 'N/A' : paybackPeriodYears,
        total_climate_amplified_benefits_npv: totalClimateAmplifiedBenefitsNPV
    };
}
