function calculate({
  impactAreaHa,
  waterwayProximityKm,
  initialWaterQuality,
  baselineBiodiversityIndex,
  ecosystemServiceValuePerHa,
  mineType,
  mitigationImplemented,
  mineLifespanYears
}) {
  // --- Input Validation and Defaulting ---
  impactAreaHa = parseFloat(impactAreaHa);
  waterwayProximityKm = parseFloat(waterwayProximityKm);
  initialWaterQuality = parseFloat(initialWaterQuality);
  baselineBiodiversityIndex = parseFloat(baselineBiodiversityIndex);
  ecosystemServiceValuePerHa = parseFloat(ecosystemServiceValuePerHa);
  mineLifespanYears = parseInt(mineLifespanYears);

  if (isNaN(impactAreaHa) || impactAreaHa <= 0) return { error: 'Direct Impact Area must be a positive number.' };
  if (isNaN(waterwayProximityKm) || waterwayProximityKm < 0) return { error: 'Proximity to Waterway must be a non-negative number.' };
  if (isNaN(initialWaterQuality) || initialWaterQuality < 0 || initialWaterQuality > 100) return { error: 'Baseline Water Quality Index must be between 0 and 100.' };
  if (isNaN(baselineBiodiversityIndex) || baselineBiodiversityIndex < 0 || baselineBiodiversityIndex > 1) return { error: 'Baseline Biodiversity Index must be between 0 and 1.' };
  if (isNaN(ecosystemServiceValuePerHa) || ecosystemServiceValuePerHa < 0) return { error: 'Ecosystem Service Value must be a non-negative number.' };
  if (isNaN(mineLifespanYears) || mineLifespanYears <= 0) return { error: 'Mine Lifespan must be a positive integer.' };

  const isSurfaceMine = mineType.toLowerCase() === 'surface';
  const hasMitigation = mitigationImplemented.toLowerCase() === 'yes';

  // --- Constants and Impact Factors ---
  // Base degradation/loss factors (hypothetical, based on typical impacts)
  const BASE_WATER_DEGRADATION_SURFACE = 35; // % reduction in quality for surface
  const BASE_WATER_DEGRADATION_UNDERGROUND = 15; // % reduction in quality for underground
  const BASE_BIODIVERSITY_LOSS_FACTOR = 0.25; // 25% base loss relative to baseline
  const INDIRECT_IMPACT_MULTIPLIER = 1.5; // Indirect impact area is 1.5x direct for services
  const REMEDIATION_COST_PER_HA = 50000; // USD/ha for post-mining remediation

  // Proximity factors: closer to waterway = higher impact
  // Using an inverse square-like relationship for impact decay with distance
  let proximityFactor = 1; // Default for very close
  if (waterwayProximityKm > 0 && waterwayProximityKm <= 0.5) proximityFactor = 1.8; // Very high impact near water
  else if (waterwayProximityKm > 0.5 && waterwayProximityKm <= 1) proximityFactor = 1.5;
  else if (waterwayProximityKm > 1 && waterwayProximityKm <= 5) proximityFactor = 1.2;
  else if (waterwayProximityKm > 5) proximityFactor = 1.0; // Farther, impact less direct

  // Initial quality/biodiversity sensitivity: lower baseline means higher proportional impact
  const waterQualitySensitivity = 1 + (100 - initialWaterQuality) / 100; // 1 to 2x impact for very poor water
  const biodiversitySensitivity = 1 + (1 - baselineBiodiversityIndex); // 1 to 2x impact for very low biodiversity

  // Mitigation effectiveness
  const MITIGATION_WATER_EFFECTIVENESS = 0.4; // Reduces degradation by 40%
  const MITIGATION_BIODIVERSITY_EFFECTIVENESS = 0.3; // Reduces loss by 30%

  // --- Calculation Steps ---

  // 1. Water Quality Degradation
  let waterDegradationBase = isSurfaceMine ? BASE_WATER_DEGRADATION_SURFACE : BASE_WATER_DEGRADATION_UNDERGROUND;
  waterDegradationBase *= proximityFactor; // Adjust by proximity
  waterDegradationBase *= waterQualitySensitivity; // Adjust by initial quality sensitivity

  if (hasMitigation) {
    waterDegradationBase *= (1 - MITIGATION_WATER_EFFECTIVENESS);
  }

  let waterQualityDegradation = Math.min(Math.max(waterDegradationBase, 0), 100); // Cap between 0-100%

  // 2. Biodiversity Loss
  let biodiversityLossBase = BASE_BIODIVERSITY_LOSS_FACTOR; // e.g., 0.25
  biodiversityLossBase *= (1 + (impactAreaHa / 1000)); // Larger area = higher base loss
  biodiversityLossBase *= proximityFactor; // Proximity to water affects aquatic/riparian biodiversity
  biodiversityLossBase *= biodiversitySensitivity; // Higher impact on already stressed ecosystems

  if (hasMitigation) {
    biodiversityLossBase *= (1 - MITIGATION_BIODIVERSITY_EFFECTIVENESS);
  }

  let biodiversityLoss = Math.min(Math.max(biodiversityLossBase, 0), 1); // Cap between 0-1 (100% loss)
  biodiversityLoss = biodiversityLoss * 100; // Convert to percentage

  // 3. Annual Ecosystem Service Loss
  // Direct loss from impacted area
  const directServiceLoss = impactAreaHa * ecosystemServiceValuePerHa;

  // Indirect loss based on quality degradation spreading beyond direct impact zone
  // Assuming indirect impact area is a multiplier of direct impact area, and its value is reduced by env degradation.
  const indirectImpactArea = impactAreaHa * INDIRECT_IMPACT_MULTIPLIER;
  const indirectServiceLossFactor = (waterQualityDegradation / 100 + biodiversityLoss / 100) / 2;
  const indirectServiceLoss = indirectImpactArea * ecosystemServiceValuePerHa * indirectServiceLossFactor;

  const annualEcosystemServiceLoss = directServiceLoss + indirectServiceLoss;

  // 4. Total Economic Impact Over Mine Lifespan
  const totalOperationalServiceLoss = annualEcosystemServiceLoss * mineLifespanYears;
  const totalRemediationCost = impactAreaHa * REMEDIATION_COST_PER_HA; // Placeholder cost

  const totalEconomicImpact = totalOperationalServiceLoss + totalRemediationCost;

  // 5. Overall Environmental Risk Score (0-100)
  // Weighted average of degradation and loss, adjusted by proximity and lifespan
  const riskWaterWeight = 0.4;
  const riskBiodiversityWeight = 0.3;
  const riskEcosystemServiceWeight = 0.2;
  const riskProximityWeight = 0.1;

  let environmentalRiskScore = 
    (waterQualityDegradation * riskWaterWeight) +
    (biodiversityLoss * riskBiodiversityWeight) +
    ((annualEcosystemServiceLoss / (ecosystemServiceValuePerHa * impactAreaHa * (1 + INDIRECT_IMPACT_MULTIPLIER))) * 100 * riskEcosystemServiceWeight) + // Normalize service loss to a % scale
    (proximityFactor * 10 * riskProximityWeight); // Proximity contributes to risk directly

  // Adjust risk further for very long mine lifespans
  if (mineLifespanYears > 50) {
      environmentalRiskScore *= 1.2;
  } else if (mineLifespanYears > 30) {
      environmentalRiskScore *= 1.1;
  }

  environmentalRiskScore = Math.min(Math.max(environmentalRiskScore, 0), 100);

  return {
    waterQualityDegradation,
    biodiversityLoss,
    annualEcosystemServiceLoss,
    totalEconomicImpact,
    environmentalRiskScore
  };
}
