const DB_C = inputs.dryBulbTemp;
const RH_percent = inputs.relativeHumidity;
const P_hPa = inputs.atmosphericPressure;

// Input validation and edge cases
if (DB_C < -40 || DB_C > 60) {
    return { error: 'Dry Bulb Temperature must be between -40°C and 60°C.' };
}
if (RH_percent < 0 || RH_percent > 100) {
    return { error: 'Relative Humidity must be between 0% and 100%.' };
}
if (P_hPa < 500 || P_hPa > 1150) {
    return { error: 'Atmospheric Pressure must be between 500 hPa and 1150 hPa.' };
}

const RH_decimal = RH_percent / 100;

// 1. Calculate Saturation Vapor Pressure (Psat) at Dry Bulb (DB_C)
// Magnus-Tetens formula: Es(T) = 6.1078 * exp( (17.27 * T) / (T + 237.3) ) (in hPa)
const Psat_DB = 6.1078 * Math.exp((17.27 * DB_C) / (237.3 + DB_C));

// 2. Calculate Actual Vapor Pressure (Pv)
const Pv = RH_decimal * Psat_DB;

// 3. Calculate Dew Point (DP_C)
let DP_C;
if (Pv <= 0.001) { // Very low vapor pressure, approximates to very low dew point
    DP_C = -60; // Cap at a very low practical value to avoid log(0) issues
} else {
    const alpha = Math.log(Pv / 6.1078);
    DP_C = (237.3 * alpha) / (17.27 - alpha);
}

// 4. Calculate Humidity Ratio (W)
let humidityRatio;
if (P_hPa - Pv <= 0.001) { // Avoid division by zero or negative denominator (implies extreme saturation or error)
    return { error: 'Actual vapor pressure is too high relative to atmospheric pressure for humidity ratio calculation. Check inputs for extreme conditions.' };
} else {
    humidityRatio = 0.62198 * Pv / (P_hPa - Pv); // 0.62198 is the ratio of molar masses of water to dry air
}

// 5. Calculate Wet Bulb Temperature (WB_C)
// Using Stull's empirical formula for WB from DB and RH (in percent)
// This formula is valid for T > 0°C (32°F) and does not explicitly account for atmospheric pressure.
// For temperatures below 0°C, the physics of psychrometrics change (ice phase vs. liquid water evaporation).
// A more complex iterative method or a specific sub-zero formula would be required for accuracy below freezing.
let WB_C;
if (DB_C < 0) {
    return { error: 'Wet Bulb calculation for temperatures below 0°C is outside the scope of this calculator\'s current approximation due to phase change considerations. Please ensure Dry Bulb Temp is above 0°C.' };
} else {
    WB_C = DB_C * Math.atan(0.151977 * Math.pow(RH_percent + 8.313659, 0.5)) +
           Math.atan(DB_C + RH_percent) -
           Math.atan(RH_percent - 1.676331) +
           0.00391838 * Math.pow(RH_percent, 1.5) * Math.atan(0.023101 * RH_percent) - 4.686035;
}

return {
    wetBulbTemp: WB_C,
    dewPointTemp: DP_C,
    humidityRatio: humidityRatio
};
