function calculateWildfireROI(inputs) {
  const propertyValue = inputs.property_value_usd;
  const annualWildfireRisk = inputs.annual_wildfire_risk_percent / 100;
  const potentialDamageUnmitigated = inputs.potential_damage_percent_unmitigated / 100;
  const initialMitigationCost = inputs.mitigation_initial_cost_usd;
  const annualMaintenanceCost = inputs.mitigation_annual_maintenance_cost_usd;
  const expectedDamageReduction = inputs.expected_damage_reduction_percent / 100;
  const annualInsuranceReduction = inputs.annual_insurance_premium_reduction_usd;
  const timeHorizon = inputs.roi_time_horizon_years;

  // Input validation and edge cases
  if (timeHorizon <= 0 || propertyValue <= 0) {
    return {
      total_mitigation_cost_usd: 0,
      total_avoided_damages_usd: 0,
      total_avoided_premiums_usd: 0,
      net_financial_benefit_usd: 0,
      roi_percent: 0,
      payback_period_years: 0
    };
  }

  // Cap percentages at 100% to prevent unrealistic scenarios
  const safeAnnualWildfireRisk = Math.min(Math.max(0, annualWildfireRisk), 1);
  const safePotentialDamageUnmitigated = Math.min(Math.max(0, potentialDamageUnmitigated), 1);
  const safeExpectedDamageReduction = Math.min(Math.max(0, expectedDamageReduction), 1);

  // 1. Calculate Total Mitigation Cost over the time horizon
  const totalMitigationCost = initialMitigationCost + (annualMaintenanceCost * timeHorizon);

  // 2. Calculate Annual Potential Damage (Unmitigated)
  const annualPotentialDamageUnmitigated = propertyValue * safeAnnualWildfireRisk * safePotentialDamageUnmitigated;

  // 3. Calculate Annual Avoided Damage
  const annualAvoidedDamage = annualPotentialDamageUnmitigated * safeExpectedDamageReduction;

  // 4. Calculate Total Avoided Damages over the time horizon
  const totalAvoidedDamages = annualAvoidedDamage * timeHorizon;

  // 5. Calculate Total Avoided Insurance Premiums over the time horizon
  const totalAvoidedPremiums = annualInsuranceReduction * timeHorizon;

  // 6. Calculate Total Financial Benefits
  const totalFinancialBenefits = totalAvoidedDamages + totalAvoidedPremiums;

  // 7. Calculate Net Financial Benefit
  const netFinancialBenefit = totalFinancialBenefits - totalMitigationCost;

  // 8. Calculate ROI Percentage
  let roiPercent = 0;
  if (totalMitigationCost > 0) {
    roiPercent = (netFinancialBenefit / totalMitigationCost) * 100;
  } else if (netFinancialBenefit > 0) {
    roiPercent = Infinity; // Pure profit with no initial cost
  }

  // 9. Calculate Payback Period
  let paybackPeriod = Infinity;
  const annualNetBenefitFlow = annualAvoidedDamage + annualInsuranceReduction - annualMaintenanceCost;
  if (annualNetBenefitFlow > 0 && initialMitigationCost > 0) {
    paybackPeriod = initialMitigationCost / annualNetBenefitFlow;
    if (paybackPeriod < 0) paybackPeriod = Infinity; // If annual cash flow is negative, no payback
  } else if (initialMitigationCost <= 0 && annualNetBenefitFlow >= 0) {
    paybackPeriod = 0; // Immediate payback if no initial cost and positive/zero net flow
  }

  // Cap payback period to time horizon or a reasonable upper limit if it's very long
  if (paybackPeriod > timeHorizon * 2) { // e.g., if it takes more than double the time horizon, consider it 'too long'
      paybackPeriod = NaN; // Represent as not applicable for very long payback periods
  }

  return {
    total_mitigation_cost_usd: totalMitigationCost,
    total_avoided_damages_usd: totalAvoidedDamages,
    total_avoided_premiums_usd: totalAvoidedPremiums,
    net_financial_benefit_usd: netFinancialBenefit,
    roi_percent: roiPercent,
    payback_period_years: paybackPeriod
  };
}
