function calculateWildfireRisk(inputs) {
  const { 
    elevation, slope, vegetationDensity, fuelType, 
    roofMaterial, sidingMaterial, defensibleSpaceCompliance, 
    prevailingWindSpeed, droughtIndex 
  } = inputs;

  let riskScore = 0;
  const recommendations = [];

  // Input validation and normalization to 0-10 scale for scoring
  const normalizedElevation = Math.min(Math.max(elevation / 300, 0), 10); // Max 3000m -> 10 score
  const normalizedSlope = Math.min(Math.max(slope / 9, 0), 10); // Max 90 deg -> 10 score
  const normalizedVegetationDensity = Math.min(Math.max(vegetationDensity / 10, 0), 10); // Max 100% -> 10 score
  const normalizedPrevailingWindSpeed = Math.min(Math.max(prevailingWindSpeed / 10, 0), 10); // Max 100 km/h -> 10 score
  const normalizedDroughtIndex = Math.min(Math.max(droughtIndex, 0), 10); // Already 0-10

  // Fuel Type Scoring
  let fuelTypeScore = 0;
  if (fuelType === 'very_low') fuelTypeScore = 1;
  else if (fuelType === 'low') fuelTypeScore = 3;
  else if (fuelType === 'moderate') fuelTypeScore = 6;
  else if (fuelType === 'high') fuelTypeScore = 9;
  if (fuelTypeScore >= 6) recommendations.push('Reduce dense, flammable vegetation, especially invasive species, and consider a fuel break.');

  // Roof Material Scoring
  let roofMaterialScore = 0;
  if (roofMaterial === 'Class A') roofMaterialScore = 1;
  else if (roofMaterial === 'Class B') roofMaterialScore = 4;
  else if (roofMaterial === 'Class C') roofMaterialScore = 7;
  else if (roofMaterial === 'Non-rated/Wood Shingle') roofMaterialScore = 10;
  if (roofMaterialScore >= 7) recommendations.push('Upgrade roof material to Class A fire-rated materials (e.g., metal, tile, asphalt shingles).');

  // Siding Material Scoring
  let sidingMaterialScore = 0;
  if (sidingMaterial === 'Non-combustible') sidingMaterialScore = 1;
  else if (sidingMaterial === 'Fire-resistant') sidingMaterialScore = 4;
  else if (sidingMaterial === 'Combustible') sidingMaterialScore = 9;
  if (sidingMaterialScore >= 7) recommendations.push('Replace combustible siding with non-combustible alternatives (e.g., stucco, fiber cement).');

  // Defensible Space Scoring (inverted: lower compliance -> higher risk score)
  const defensibleSpaceScore = Math.min(Math.max((100 - defensibleSpaceCompliance) / 10, 0), 10);
  if (defensibleSpaceCompliance < 70) recommendations.push('Significantly improve defensible space zones around the property, clearing vegetation and ladder fuels.');

  // Weights for each factor (sum to 100%)
  const weights = {
    elevation: 4, 
    slope: 8,
    vegetationDensity: 14,
    fuelType: 14,
    roofMaterial: 18,
    sidingMaterial: 9,
    defensibleSpace: 18,
    prevailingWindSpeed: 8,
    droughtIndex: 7
  };

  // Calculate weighted risk score
  riskScore += normalizedElevation * weights.elevation;
  if (normalizedElevation > 7) recommendations.push('Assess specific wind patterns and topography-driven fire behavior at higher elevations.');

  riskScore += normalizedSlope * weights.slope;
  if (normalizedSlope > 5) recommendations.push('Mitigate risk on steep slopes by reducing vegetation and ensuring robust erosion control.');

  riskScore += normalizedVegetationDensity * weights.vegetationDensity;
  if (normalizedVegetationDensity > 5) recommendations.push('Reduce overall vegetation density within 30 meters of the property, focusing on fuel continuity.');

  riskScore += fuelTypeScore * weights.fuelType;
  riskScore += roofMaterialScore * weights.roofMaterial;
  riskScore += sidingMaterialScore * weights.sidingMaterial;
  riskScore += defensibleSpaceScore * weights.defensibleSpace;

  riskScore += normalizedPrevailingWindSpeed * weights.prevailingWindSpeed;
  if (normalizedPrevailingWindSpeed > 5) recommendations.push('Consider ember-resistant vents and window screens, as wind-driven embers are a major ignition source.');

  riskScore += normalizedDroughtIndex * weights.droughtIndex;
  if (normalizedDroughtIndex > 6) recommendations.push('Stay informed on local drought conditions and implement water-wise landscaping, maintaining plant health to reduce flammability.');

  // Scale the total risk score from 0-100 (since individual scores are 0-10 and weights sum to 100)
  const finalRiskScore = Math.round(riskScore / 10);

  let riskCategory;
  if (finalRiskScore <= 25) {
    riskCategory = 'Low';
  } else if (finalRiskScore <= 50) {
    riskCategory = 'Moderate';
  } else if (finalRiskScore <= 75) {
    riskCategory = 'High';
  } else {
    riskCategory = 'Extreme';
  }

  // Ensure unique recommendations
  const uniqueRecommendations = [...new Set(recommendations)];
  if (uniqueRecommendations.length === 0) {
    uniqueRecommendations.push('Maintain current wildfire mitigation efforts diligently. Your property exhibits characteristics of lower wildfire risk.');
  }

  return {
    wildfireRiskScore: finalRiskScore,
    riskCategory: riskCategory,
    mitigationRecommendations: uniqueRecommendations.join('\n- ')
  };
}
