function calculate({
      propertySizeSqFt,
      standardConstructionCostPerSqFt,
      fireResistantMaterialPremiumPercent,
      propertyAcreage,
      defensibleSpaceLandscapingCostPerAcre,
      estimatedAnnualStandardInsurancePremium,
      insurancePremiumReductionPercent,
      estimatedAnnualStandardMaintenanceCost,
      annualMaintenanceSavingsPercent,
      timeHorizonYears,
      wildfireDamageMitigationPercent,
      averageWildfireEventCost,
    }) {
      // Input validation and clamping
      propertySizeSqFt = Math.max(0, propertySizeSqFt);
      standardConstructionCostPerSqFt = Math.max(0, standardConstructionCostPerSqFt);
      fireResistantMaterialPremiumPercent = Math.max(0, Math.min(100, fireResistantMaterialPremiumPercent));
      propertyAcreage = Math.max(0, propertyAcreage);
      defensibleSpaceLandscapingCostPerAcre = Math.max(0, defensibleSpaceLandscapingCostPerAcre);
      estimatedAnnualStandardInsurancePremium = Math.max(0, estimatedAnnualStandardInsurancePremium);
      insurancePremiumReductionPercent = Math.max(0, Math.min(100, insurancePremiumReductionPercent));
      estimatedAnnualStandardMaintenanceCost = Math.max(0, estimatedAnnualStandardMaintenanceCost);
      annualMaintenanceSavingsPercent = Math.max(0, Math.min(100, annualMaintenanceSavingsPercent));
      timeHorizonYears = Math.max(1, timeHorizonYears);
      wildfireDamageMitigationPercent = Math.max(0, Math.min(100, wildfireDamageMitigationPercent));
      averageWildfireEventCost = Math.max(0, averageWildfireEventCost);

      // Step 1: Calculate Upfront Costs
      const standardConstructionCost = propertySizeSqFt * standardConstructionCostPerSqFt;
      const fireResistantMaterialPremiumCost = standardConstructionCost * (fireResistantMaterialPremiumPercent / 100);
      const defensibleSpaceCost = propertyAcreage * defensibleSpaceLandscapingCostPerAcre;

      const initialInvestmentCost = fireResistantMaterialPremiumCost + defensibleSpaceCost;
      const totalUpfrontCost = standardConstructionCost + initialInvestmentCost;

      // Step 2: Calculate Annual Operating Savings
      const annualInsuranceSavings = estimatedAnnualStandardInsurancePremium * (insurancePremiumReductionPercent / 100);
      const actualAnnualMaintenanceSavings = estimatedAnnualStandardMaintenanceCost * (annualMaintenanceSavingsPercent / 100);
      const annualOperatingSavings = annualInsuranceSavings + actualAnnualMaintenanceSavings;

      // Step 3: Calculate Cumulative Long-Term Savings
      const cumulativeLongTermSavings = annualOperatingSavings * timeHorizonYears;

      // Step 4: Calculate Potential Damage Avoided
      const potentialDamageAvoided = averageWildfireEventCost * (wildfireDamageMitigationPercent / 100);

      // Step 5: Calculate Net Benefit Over Time Horizon
      // The net benefit includes upfront additional investment, ongoing savings, and the one-time potential damage avoidance.
      const netBenefitOverTimeHorizon = cumulativeLongTermSavings + potentialDamageAvoided - initialInvestmentCost;

      return {
        initialInvestmentCost,
        totalUpfrontCost,
        annualOperatingSavings,
        cumulativeLongTermSavings,
        potentialDamageAvoided,
        netBenefitOverTimeHorizon,
      };
    }
