const propertyValue = inputs.propertyValue || 0;
const propertyArea = inputs.propertyArea || 0;
const wildfireRiskLevel = inputs.wildfireRiskLevel || 1;
const mitigationStrategy = inputs.mitigationStrategy || 1;
const localCostIndex = inputs.localCostIndex || 1.0;
const insuranceReduction = inputs.insuranceReduction || 0; // %
const roiYears = inputs.roiYears || 10;
const carbonCreditValue = inputs.carbonCreditValue || 0; // USD/ton CO2e
const environmentalImpactWeight = inputs.environmentalImpactWeight || 50; // %

if (propertyValue <= 0 || propertyArea <= 0 || roiYears <= 0 || localCostIndex <= 0 || carbonCreditValue < 0 || environmentalImpactWeight < 0 || environmentalImpactWeight > 100) {
    return {
        totalMitigationCost: 0,
        annualRiskReductionBenefit: 0,
        totalEnvironmentalBenefit: 0,
        netBenefit: 0,
        returnOnInvestment: 0,
        paybackPeriod: 0,
        propertyValueIncrease: 0
    };
}

// Mappings for risk, mitigation effectiveness, costs, and environmental impacts
// Annual probability of significant wildfire impact
const riskProbabilities = {
    1: 0.0005, // Low: 0.05% annual chance
    2: 0.005,  // Moderate: 0.5% annual chance
    3: 0.015,  // High: 1.5% annual chance
    4: 0.05,   // Very High: 5% annual chance
    5: 0.10    // Extreme: 10% annual chance
};
const currentProb = riskProbabilities[wildfireRiskLevel] || riskProbabilities[1];

// Factor by which original risk is reduced due to mitigation (e.g., 0.3 means risk is reduced by 30%)
const mitigationEffectiveness = {
    1: 0.30,   // Basic Defensible Space: 30% risk reduction
    2: 0.60,   // Advanced Hardening: 60% risk reduction
    3: 0.85    // Comprehensive Approach: 85% risk reduction
};
const riskReductionFactor = mitigationEffectiveness[mitigationStrategy] || mitigationEffectiveness[1];
const postMitigationProb = currentProb * (1 - riskReductionFactor);

// Base costs per acre (USD) for initial setup
const baseInitialCostsPerAcre = {
    1: 1500, // Basic: vegetation clearing Zone 1-2
    2: 7500, // Advanced: Basic + structural hardening (e.g., vents, minor roofing, ember-resistant materials)
    3: 15000 // Comprehensive: Advanced + extensive landscaping modification, specialized materials, community-level planning
};
// Annual maintenance costs per acre (USD)
const annualMaintenanceCostsPerAcre = {
    1: 150,  // Basic: annual cleanup, minor trimming
    2: 300,  // Advanced: more detailed upkeep, structural checks
    3: 500   // Comprehensive: specialized landscape maintenance, regular inspections
};

// Environmental impact (CO2e tons)
// Carbon sequestration uplift from improved ecosystem health, per acre per year (positive impact)
const carbonSequestrationPerAcrePerYear = {
    1: 0.1, // Basic: minor improvement in plant health/growth
    2: 0.5, // Advanced: moderate improvement, strategic fire-resilient planting
    3: 1.0  // Comprehensive: significant ecological restoration, robust native species, soil health
};
// Avoided CO2e emissions from preventing a single significant wildfire event (tons CO2e if fire is prevented)
const avoidedEmissionsFromPreventedWildfire = {
    1: 500,  // Basic mitigation may prevent smaller, less intense fires
    2: 2000, // Advanced mitigation helps prevent more significant fires
    3: 5000  // Comprehensive mitigation aims to prevent major, catastrophic events
};


// 1. Calculate Total Mitigation Cost
const initialCost = (baseInitialCostsPerAcre[mitigationStrategy] || baseInitialCostsPerAcre[1]) * propertyArea * localCostIndex;
const totalAnnualMaintenanceCost = (annualMaintenanceCostsPerAcre[mitigationStrategy] || annualMaintenanceCostsPerAcre[1]) * propertyArea * localCostIndex;
const totalMitigationCost = initialCost + (totalAnnualMaintenanceCost * roiYears);

// 2. Calculate Annual Risk Reduction Benefit (Financial)
// Value of avoided loss due to reduced probability of a damaging event
const avoidedLossBenefit = propertyValue * (currentProb - postMitigationProb);
// Estimated annual insurance premium reduction. Assume average annual premium is 0.5% of property value for calculation baseline.
const averageAnnualPremium = propertyValue * 0.005;
const insuranceBenefit = averageAnnualPremium * (insuranceReduction / 100);
const annualFinancialRiskReductionBenefit = avoidedLossBenefit + insuranceBenefit;

// 3. Calculate Total Environmental Benefit (over analysis period)
let totalEnvironmentalBenefit = 0;
if (carbonCreditValue > 0) {
    // Benefit from ongoing carbon sequestration due to healthier, managed ecosystem
    const sequestrationBenefitTons = (carbonSequestrationPerAcrePerYear[mitigationStrategy] || carbonSequestrationPerAcrePerYear[1]) * propertyArea * roiYears;

    // Benefit from avoided emissions if a significant wildfire is prevented (weighted by probability difference) over the analysis period.
    // We're annualizing this for the benefit calculation, assuming the *potential* for avoided emissions is present each year.
    const avoidedFireEmissionsTonsAnnualized = (avoidedEmissionsFromPreventedWildfire[mitigationStrategy] || avoidedEmissionsFromPreventedWildfire[1]) * (currentProb - postMitigationProb);
    const totalAvoidedFireEmissionsTonsOverPeriod = avoidedFireEmissionsTonsAnnualized * roiYears;

    totalEnvironmentalBenefit = (sequestrationBenefitTons + totalAvoidedFireEmissionsTonsOverPeriod) * carbonCreditValue;
}

// 4. Calculate Total Net Benefit (over analysis period)
const totalFinancialBenefitOverPeriod = annualFinancialRiskReductionBenefit * roiYears;
const weightedEnvironmentalBenefitOverPeriod = totalEnvironmentalBenefit * (environmentalImpactWeight / 100);
const totalGrossBenefitOverPeriod = totalFinancialBenefitOverPeriod + weightedEnvironmentalBenefitOverPeriod;
const netBenefit = totalGrossBenefitOverPeriod - totalMitigationCost;

// 5. Calculate Return on Investment (ROI)
let returnOnInvestment = 0;
if (totalMitigationCost > 0) {
    returnOnInvestment = (netBenefit / totalMitigationCost) * 100;
} else if (netBenefit > 0) {
    returnOnInvestment = Infinity; // Infinite ROI if there's benefit with no cost
}

// 6. Calculate Payback Period
let paybackPeriod = Infinity;
const annualizedCombinedBenefit = annualFinancialRiskReductionBenefit + (totalEnvironmentalBenefit / roiYears * (environmentalImpactWeight / 100));
if (annualizedCombinedBenefit > 0) {
    paybackPeriod = totalMitigationCost / annualizedCombinedBenefit;
    if (paybackPeriod < 0) paybackPeriod = Infinity; // If costs are covered by what are effectively 'negative benefits' or if net cost is negative, it's effectively infinite for a positive investment scenario.
    if (paybackPeriod > roiYears) paybackPeriod = Infinity; // If payback takes longer than the analysis period
} else if (totalMitigationCost <= 0 && annualizedCombinedBenefit >= 0) {
    paybackPeriod = 0; // Immediate payback if no cost and positive/neutral benefit
}

// 7. Estimated Property Value Increase
// Mitigated properties often command higher market values due to reduced risk and improved aesthetics/safety.
// Assume a base increase of 2% for any mitigation, up to an additional 8% proportional to risk reduction achieved.
const baseValueIncreaseFactor = 0.02; // Minimum expected increase for any recognized mitigation
const maxAdditionalValueIncreaseFactor = 0.08; // Maximum additional increase for comprehensive mitigation
const propertyValueIncrease = propertyValue * (baseValueIncreaseFactor + (maxAdditionalValueIncreaseFactor * riskReductionFactor));

return {
    totalMitigationCost: totalMitigationCost,
    annualRiskReductionBenefit: annualFinancialRiskReductionBenefit,
    totalEnvironmentalBenefit: totalEnvironmentalBenefit,
    netBenefit: netBenefit,
    returnOnInvestment: returnOnInvestment,
    paybackPeriod: paybackPeriod,
    propertyValueIncrease: propertyValueIncrease
};
