function calculate(inputs) {
  const { location_type, building_type, foundation_type, roof_type, property_age, projected_snowfall, projected_ice, projected_wind_speed } = inputs;

  // --- 1. Validate Inputs & Set Defaults ---
  if (isNaN(projected_snowfall) || projected_snowfall < 0 ||
      isNaN(projected_ice) || projected_ice < 0 ||
      isNaN(projected_wind_speed) || projected_wind_speed < 0 ||
      isNaN(property_age) || property_age < 0) {
    return { 
      overall_risk_score: 'N/A', 
      estimated_damage_cost_min: 'N/A', 
      estimated_damage_cost_max: 'N/A', 
      suggested_mitigation_cost: 'N/A', 
      primary_risk_factor: 'Invalid Inputs', 
      recommendations: 'Please ensure all numerical inputs are valid and non-negative.' 
    };
  }

  let baseRisk = 0;
  let weatherImpact = 0;
  let structuralVulnerability = 0;
  let primaryRiskFactor = 'General Vulnerability';
  let recommendations = [];

  // --- 2. Base Risk by Location Type ---
  switch (location_type) {
    case 'northeast_us': baseRisk = 25; break;
    case 'mid_atlantic_us': baseRisk = 20; break;
    case 'midwest_us': baseRisk = 30; break;
    case 'mountain_west_us': baseRisk = 35; break;
    case 'pacific_northwest_us': baseRisk = 15; break;
    case 'northern_plains_us': baseRisk = 40; break;
    case 'southeast_us': baseRisk = 10; break;
    case 'canada_east': baseRisk = 35; break;
    case 'canada_west': baseRisk = 30; break;
    default: baseRisk = 15; // Default for unknown/other
  }

  // --- 3. Weather Impact Calculation ---
  // Snowfall Impact (linear up to 24 inches, then logarithmic/capped)
  let snowImpact = 0;
  if (projected_snowfall > 0) {
    if (projected_snowfall <= 12) snowImpact = projected_snowfall * 1.5; // 18 pts for 12 inches
    else if (projected_snowfall <= 24) snowImpact = 18 + (projected_snowfall - 12) * 1.0; // 30 pts for 24 inches
    else snowImpact = 30 + Math.log(projected_snowfall - 24 + 1) * 5; // Logarithmic increase for heavy snow, diminishing returns on risk score after a point
    snowImpact = Math.min(snowImpact, 50); // Cap snow impact score
    if (snowImpact > weatherImpact) { weatherImpact = snowImpact; primaryRiskFactor = 'Heavy Snowfall'; recommendations.push('Ensure roof is structurally sound and consider professional snow removal for heavy accumulation.'); }
  }

  // Ice Accumulation Impact (very high impact, even small amounts)
  let iceImpact = 0;
  if (projected_ice > 0) {
    iceImpact = projected_ice * 40; // 0.25 inches -> 10 pts, 1 inch -> 40 pts
    iceImpact = Math.min(iceImpact, 60); // Cap ice impact score
    if (iceImpact > weatherImpact) { weatherImpact = iceImpact; primaryRiskFactor = 'Ice Accumulation'; recommendations.push('Trim trees near property, secure gutters, and prepare for potential power outages due to ice.'); }
  }

  // Wind Speed Impact
  let windImpact = 0;
  if (projected_wind_speed > 0) {
    if (projected_wind_speed <= 30) windImpact = 0; // Minor winds no impact
    else if (projected_wind_speed <= 50) windImpact = (projected_wind_speed - 30) * 0.75; // 15 pts for 50 mph
    else windImpact = 15 + (projected_wind_speed - 50) * 0.5; // Additional points for higher winds
    windImpact = Math.min(windImpact, 35); // Cap wind impact score
    if (windImpact > weatherImpact) { weatherImpact = windImpact; primaryRiskFactor = 'High Winds'; recommendations.push('Secure loose outdoor items, inspect roof and siding for weaknesses, and reinforce entry points.'); }
  }

  // Combine weather impacts, prioritizing the highest single factor for the score
  weatherImpact = Math.max(snowImpact, iceImpact, windImpact);

  // --- 4. Structural Vulnerability Calculation ---
  // Property Age
  let ageFactor = 0;
  if (property_age > 50) ageFactor = (property_age - 50) * 0.2; // 10 pts for 100 years old
  else if (property_age > 20) ageFactor = (property_age - 20) * 0.1; // 3 pts for 50 years old
  ageFactor = Math.min(ageFactor, 15); // Cap age factor
  if (ageFactor > structuralVulnerability) { structuralVulnerability = ageFactor; if (primaryRiskFactor === 'General Vulnerability') primaryRiskFactor = 'Aging Infrastructure'; recommendations.push('Consider a professional property inspection to assess the integrity of older structures.'); }

  // Building Type
  let buildingTypeFactor = 0;
  switch (building_type) {
    case 'single_family_residential': buildingTypeFactor = 5; break;
    case 'multi_family_residential': buildingTypeFactor = 7; break;
    case 'commercial_light': buildingTypeFactor = 8; break;
    case 'commercial_heavy': buildingTypeFactor = 10; break;
    case 'industrial': buildingTypeFactor = 12; break;
    case 'historical_heritage': buildingTypeFactor = 15; break;
  }
  structuralVulnerability += buildingTypeFactor;

  // Foundation Type (primarily for water/freeze-thaw damage)
  let foundationFactor = 0;
  switch (foundation_type) {
    case 'slab_on_grade': foundationFactor = 5; recommendations.push('Ensure proper drainage away from the foundation to prevent freeze-thaw cracking.'); break;
    case 'crawl_space': foundationFactor = 10; recommendations.push('Insulate crawl space pipes and ensure proper ventilation to prevent freezing and moisture damage.'); break;
    case 'full_basement_finished': foundationFactor = 8; recommendations.push('Check basement for cracks, seal windows, and ensure sump pump is operational.'); break;
    case 'full_basement_unfinished': foundationFactor = 6; recommendations.push('Ensure proper drainage and seal any foundation cracks.'); break;
    case 'pier_and_beam': foundationFactor = 7; recommendations.push('Inspect piers for stability and ensure adequate skirting to protect utilities.'); break;
  }
  structuralVulnerability += foundationFactor;

  // Roof Type (for snow load, ice dams, wind uplift)
  let roofFactor = 0;
  switch (roof_type) {
    case 'gable_asphalt': roofFactor = 5; recommendations.push('Ensure gutters are clear to prevent ice dam formation.'); break;
    case 'hip_asphalt': roofFactor = 4; recommendations.push('Ensure gutters are clear to prevent ice dam formation.'); break;
    case 'flat_membrane': roofFactor = 15; recommendations.push('Regularly inspect membrane for punctures and ensure proper drainage to prevent ponding and excessive snow load.'); if (primaryRiskFactor === 'General Vulnerability') primaryRiskFactor = 'Flat Roof Snow/Ice Load'; break;
    case 'slanted_metal': roofFactor = 3; recommendations.push('Ensure snow guards are in place if desired to prevent large snow slides onto entryways.'); break;
    case 'wood_shingle_shake': roofFactor = 10; recommendations.push('Inspect shingles for damage and ensure proper flashing to prevent water intrusion.'); break;
    case 'tile': roofFactor = 8; recommendations.push('Inspect tiles for cracks and ensure proper underlayment for water shedding.'); break;
  }
  structuralVulnerability += roofFactor;

  // --- 5. Combine All Scores ---
  let overallRiskScore = baseRisk + weatherImpact + structuralVulnerability;
  overallRiskScore = Math.round(Math.min(overallRiskScore, 100)); // Cap at 100

  // --- 6. Estimate Damage Costs & Mitigation Costs ---
  let baseCostFactor = 0;
  switch (building_type) {
    case 'single_family_residential': baseCostFactor = 5000; break;
    case 'multi_family_residential': baseCostFactor = 15000; break;
    case 'commercial_light': baseCostFactor = 10000; break;
    case 'commercial_heavy': baseCostFactor = 30000; break;
    case 'industrial': baseCostFactor = 50000; break;
    case 'historical_heritage': baseCostFactor = 20000; break;
  }

  let damageMultiplier = overallRiskScore / 100;
  let estimatedDamageCostMin = baseCostFactor * 0.5 * damageMultiplier;
  let estimatedDamageCostMax = baseCostFactor * 2.0 * damageMultiplier;

  // Adjust for extreme weather, significantly increasing potential damage
  if (projected_snowfall >= 24) estimatedDamageCostMax *= 1.2;
  if (projected_ice >= 0.5) estimatedDamageCostMax *= 1.5;
  if (projected_wind_speed >= 60) estimatedDamageCostMax *= 1.3;

  // Mitigation cost is proportional to risk and complexity
  let mitigationCostFactor = 0;
  switch (building_type) {
    case 'single_family_residential': mitigationCostFactor = 1500; break;
    case 'multi_family_residential': mitigationCostFactor = 4000; break;
    case 'commercial_light': mitigationCostFactor = 3000; break;
    case 'commercial_heavy': mitigationCostFactor = 8000; break;
    case 'industrial': mitigationCostFactor = 10000; break;
    case 'historical_heritage': mitigationCostFactor = 5000; break;
  }
  let suggestedMitigationCost = mitigationCostFactor + (overallRiskScore / 100) * mitigationCostFactor * 2;
  if (mitigationCostFactor > 0) suggestedMitigationCost = Math.max(suggestedMitigationCost, 500);

  // --- 7. Finalize Recommendations ---
  if (recommendations.length === 0) {
    recommendations.push('Regular maintenance and monitoring are key to winter storm preparedness.');
  }

  return {
    overall_risk_score: overallRiskScore,
    estimated_damage_cost_min: parseFloat(estimatedDamageCostMin.toFixed(2)),
    estimated_damage_cost_max: parseFloat(estimatedDamageCostMax.toFixed(2)),
    suggested_mitigation_cost: parseFloat(suggestedMitigationCost.toFixed(2)),
    primary_risk_factor: primaryRiskFactor,
    recommendations: recommendations.join(' ')
  };
}
