const validInfraTypes = ['Roads', 'Power Grid', 'Water Treatment', 'Telecommunications'];
if (!validInfraTypes.includes(infrastructureType)) {
    infrastructureType = 'Roads'; // Default to Roads if invalid type provided
}

// Input validation and clamping
let s = Math.max(0, forecastedSnowfall); // Snowfall in inches
let i = Math.max(0, forecastedIceAccumulation); // Ice accumulation in inches
let w = Math.max(0, forecastedWindSpeed); // Wind speed in MPH
let d = Math.max(1, stormDuration); // Storm duration in hours, min 1 hour
let r = Math.min(10, Math.max(0, resilienceLevel)); // Resilience 0-10
let a = Math.max(0, infrastructureAge); // Infrastructure age in years
let c = Math.min(2.0, Math.max(0.5, localRecoveryCostFactor)); // Local cost factor 0.5-2.0

// Coefficients for impact based on infrastructure type
const impactCoefficients = {
    'Roads': {
        snow: 0.8, ice: 2.5, wind: 0.1, duration: 0.08, baseCostFactor: 0.8, baseDowntimeFactor: 0.6
    },
    'Power Grid': {
        snow: 1.5, ice: 6.0, wind: 0.7, duration: 0.15, baseCostFactor: 2.5, baseDowntimeFactor: 1.8
    },
    'Water Treatment': {
        snow: 0.5, ice: 1.0, wind: 0.2, duration: 0.05, baseCostFactor: 1.2, baseDowntimeFactor: 0.9
    },
    'Telecommunications': {
        snow: 1.0, ice: 3.5, wind: 0.5, duration: 0.12, baseCostFactor: 1.5, baseDowntimeFactor: 1.2
    }
};

const infra = impactCoefficients[infrastructureType];

// Step 1: Calculate Raw Storm Impact Score (weighted sum)
// Ice has a higher multiplier due to its destructive nature (weight, power lines, trees)
// Wind impact threshold: significant impact starts after 25 MPH, then scales
let rawImpact = (s * infra.snow) +
                (i * infra.ice * 5) + // Ice accumulation is severely impactful
                (Math.max(0, w - 25) * infra.wind * 0.2) + // Wind impact scales after 25MPH
                (d * infra.duration * 0.5); // Duration contributes to sustained impact

// Apply a non-linear scaling to raw impact to prevent extreme linear values
rawImpact = Math.min(rawImpact * 1.5, 300); // Cap initial raw impact

// Step 2: Adjust for Infrastructure Age
// Older infrastructure is more vulnerable; every 10 years adds a small percentage vulnerability
const ageVulnerabilityFactor = 1 + (a / 50 * 0.2); // 50-year-old infrastructure is 20% more vulnerable
rawImpact *= ageVulnerabilityFactor;

// Step 3: Adjust for Existing Resilience Level
// Higher resilience significantly reduces impact (e.g., 0-70% reduction)
const resilienceReductionFactor = 1 - (r / 10 * 0.75); // Max 75% reduction for resilience level 10
let adjustedImpact = rawImpact * resilienceReductionFactor;

// Ensure adjusted impact doesn't go below a certain floor for substantial storms
if (adjustedImpact < 10 && (s > 10 || i > 0.1 || w > 35)) {
    adjustedImpact = 10; // Minimum impact floor for moderate storms
}

// Step 4: Normalize to Operational Impact Score (0-100)
let operationalImpactScore = Math.min(Math.max(0, adjustedImpact / 3), 100);

// Ensure a minimum score for very low calculated impact but existing storm parameters
if (operationalImpactScore < 5 && (s > 5 || i > 0.05 || w > 20)) {
    operationalImpactScore = 5; // A very minor impact for small but present storm events
}

// Step 5: Estimate Recovery Cost
// Base costs vary significantly by infrastructure type and are scaled by operational impact
const baseCostPerImpactPoint = {
    'Roads': 25000, 
    'Power Grid': 150000, 
    'Water Treatment': 50000, 
    'Telecommunications': 75000
};
let estimatedRecoveryCost = (operationalImpactScore / 100) * baseCostPerImpactPoint[infrastructureType] * c * (1 + (d / 24 * 0.1)); // Longer duration slightly increases cost

// Set a minimum recovery cost for any calculated impact
estimatedRecoveryCost = Math.max(estimatedRecoveryCost, 5000);

// Step 6: Estimate Downtime
// Base downtime in hours per impact point, scaled by operational impact
const baseDowntimePerImpactPoint = {
    'Roads': 24, 
    'Power Grid': 120, 
    'Water Treatment': 48, 
    'Telecommunications': 72
};
let estimatedDowntime = (operationalImpactScore / 100) * baseDowntimePerImpactPoint[infrastructureType];

// Add a penalty for extremely long durations for power/telecom
if ((infrastructureType === 'Power Grid' || infrastructureType === 'Telecommunications') && d > 48) {
    estimatedDowntime *= (1 + (d - 48) / 24 * 0.15); // Add 15% downtime for every day over 2 days
}

// Set a minimum and maximum downtime
estimatedDowntime = Math.max(estimatedDowntime, 2); // Minimum 2 hours
estimatedDowntime = Math.min(estimatedDowntime, 720); // Maximum 30 days (720 hours)

return {
    operationalImpactScore: parseFloat(operationalImpactScore.toFixed(1)),
    estimatedRecoveryCost: parseFloat(estimatedRecoveryCost.toFixed(0)),
    estimatedDowntime: parseFloat(estimatedDowntime.toFixed(1))
};
