const familySize = inputs.familySize || 1;
const stormDurationDays = Math.max(inputs.stormDurationDays || 1, 1); // Minimum 1 day
const hasPets = inputs.hasPets || 0; // 0 for No, 1 for Yes
const budgetType = Math.min(Math.max(inputs.budgetType || 0, 0), 2); // Cap between 0 and 2
const hasBackupGenerator = inputs.hasBackupGenerator || 0; // 0 for No, 1 for Yes
const localCostIndex = Math.max(inputs.localCostIndex || 1.0, 0.5); // Minimum local cost index

// --- Per-person, per-day constants ---
const WATER_PER_PERSON_LITERS = 4; // Liters for drinking and basic sanitation
const CALORIES_PER_PERSON_DAY = 2200;

// --- Cost Multipliers based on budgetType ---
// Food cost per person per day
const FOOD_COST_DAILY_BASIC = 10;
const FOOD_COST_DAILY_STANDARD = 15;
const FOOD_COST_DAILY_PREMIUM = 25;
const foodCostDaily = budgetType === 0 ? FOOD_COST_DAILY_BASIC :
                      budgetType === 1 ? FOOD_COST_DAILY_STANDARD :
                      FOOD_COST_DAILY_PREMIUM;

// Emergency supplies cost per person (one-time initial purchase)
const EMERGENCY_SUPPLIES_COST_BASIC = 75;
const EMERGENCY_SUPPLIES_COST_STANDARD = 125;
const EMERGENCY_SUPPLIES_COST_PREMIUM = 200;
const emergencySuppliesCostOneTime = budgetType === 0 ? EMERGENCY_SUPPLIES_COST_BASIC :
                                    budgetType === 1 ? EMERGENCY_SUPPLIES_COST_STANDARD :
                                    EMERGENCY_SUPPLIES_COST_PREMIUM;

// Water storage cost (e.g., bottled water, storage containers)
const WATER_COST_PER_LITER = 0.5; // Average cost for bottled water or storage containers

// --- Pet specific costs (simplified, assuming one average pet for pet costs) ---
const PET_WATER_ADD_PERCENT = 0.25; // Add 25% of human water needs for a pet
const PET_FOOD_DAILY_COST = 5;
const PET_SUPPLIES_ONE_TIME_COST = 50; // Leash, carrier, extra food/bowl

// --- Alternative Power ---
const POWER_WH_PER_PERSON_DAY_BASIC = 100; // For charging phones, small lights
const POWER_WH_PER_PERSON_DAY_STANDARD = 200; // Phones, laptops, small fan/heater bursts
const POWER_WH_PER_PERSON_DAY_PREMIUM = 400; // More extensive use, small appliances
const powerWhDaily = budgetType === 0 ? POWER_WH_PER_PERSON_DAY_BASIC :
                     budgetType === 1 ? POWER_WH_PER_PERSON_DAY_STANDARD :
                     POWER_WH_PER_PERSON_DAY_PREMIUM;

// Cost for new portable power station (one-time if needed)
const PORTABLE_POWER_STATION_COST_BASIC = 300; // Smaller capacity
const PORTABLE_POWER_STATION_COST_STANDARD = 600; // Mid-capacity
const PORTABLE_POWER_STATION_COST_PREMIUM = 1000; // Larger capacity
const portablePowerStationCost = budgetType === 0 ? PORTABLE_POWER_STATION_COST_BASIC :
                                budgetType === 1 ? PORTABLE_POWER_STATION_COST_STANDARD :
                                PORTABLE_POWER_STATION_COST_PREMIUM;

// Fuel cost for existing generator (per day)
const GENERATOR_FUEL_COST_DAILY = 20;

// --- Calculations ---

// 1. Water
const totalWaterNeededLiters = familySize * WATER_PER_PERSON_LITERS * stormDurationDays +
                               (hasPets ? (familySize * WATER_PER_PERSON_LITERS * PET_WATER_ADD_PERCENT * stormDurationDays) : 0);
const costWater = totalWaterNeededLiters * WATER_COST_PER_LITER;

// 2. Food
const totalFoodCalories = familySize * CALORIES_PER_PERSON_DAY * stormDurationDays;
const costFood = (familySize * foodCostDaily * stormDurationDays) +
                 (hasPets ? (PET_FOOD_DAILY_COST * stormDurationDays) : 0); // Assuming one pet for simplicity of pet food cost

// 3. Emergency Supplies (one-time purchase)
const costEmergencySupplies = (familySize * emergencySuppliesCostOneTime) +
                              (hasPets ? PET_SUPPLIES_ONE_TIME_COST : 0);

// 4. Alternative Power
const powerBatteryCapacityWh = familySize * powerWhDaily * stormDurationDays;
let costPower = 0;
if (hasBackupGenerator === 0) { // User needs to buy a power solution
    costPower = portablePowerStationCost;
} else { // User has a generator, account for fuel
    costPower = GENERATOR_FUEL_COST_DAILY * stormDurationDays;
}

// 5. Buffer for miscellaneous (e.g., extra batteries, entertainment, personal hygiene)
const MISC_BUFFER_PERCENT = 0.15; // 15% buffer
const subtotalCost = costFood + costWater + costEmergencySupplies + costPower;
const costMiscellaneous = subtotalCost * MISC_BUFFER_PERCENT;

// Total Cost, applying local cost index
const totalEstimatedCost = (subtotalCost + costMiscellaneous) * localCostIndex;

return {
    totalEstimatedCost: totalEstimatedCost,
    totalWaterNeededLiters: totalWaterNeededLiters,
    totalFoodCalories: totalFoodCalories,
    powerBatteryCapacityWh: powerBatteryCapacityWh,
    costFood: costFood,
    costWater: costWater,
    costPower: costPower,
    costEmergencySupplies: costEmergencySupplies,
    costMiscellaneous: costMiscellaneous,
};
