function calculatePreparedness(inputs) {
  let score = 0;

  // 1. Emergency Water Supply (max 15 pts)
  if (inputs.emergencyWaterDays < 1) score += 0;
  else if (inputs.emergencyWaterDays <= 2) score += 5;
  else if (inputs.emergencyWaterDays <= 4) score += 10;
  else score += 15;

  // 2. Emergency Food Supply (max 15 pts)
  if (inputs.emergencyFoodDays < 1) score += 0;
  else if (inputs.emergencyFoodDays <= 2) score += 5;
  else if (inputs.emergencyFoodDays <= 4) score += 10;
  else score += 15;

  // 3. Alternative Heating Source (max 10 pts)
  score += inputs.alternativeHeatSource === 1 ? 10 : 0;

  // 4. Power Backup (max 15 pts)
  switch (inputs.powerBackup) {
    case 0: score += 0; break; // None
    case 1: score += 5; break; // Battery/UPS
    case 2: score += 10; break; // Portable Gen
    case 3: score += 15; break; // Whole-home Gen
  }

  // 5. First-Aid Kit (max 5 pts)
  score += inputs.firstAidKit === 1 ? 5 : 0;

  // 6. Communication Plan (max 5 pts)
  score += inputs.communicationPlan === 1 ? 5 : 0;

  // 7. Vehicle Preparedness (max 5 pts)
  if (inputs.vehiclePreparedness === 1 || inputs.vehiclePreparedness === 2) {
    score += 5; // Yes or N/A (not penalized for no vehicle)
  }

  // 8. Home Insulation Rating (max 15 pts)
  switch (inputs.homeInsulationRating) {
    case 0: score += 0; break; // Poor
    case 1: score += 5; break; // Average
    case 2: score += 10; break; // Good
    case 3: score += 15; break; // Excellent
  }

  // 9. Medication Supply Days (max 10 pts)
  if (inputs.medicationSupplyDays < 3) score += 0;
  else if (inputs.medicationSupplyDays <= 7) score += 5;
  else score += 10;

  // 10. Pet Preparedness (max 5 pts)
  if (inputs.petPreparedness === 1 || inputs.petPreparedness === 2) {
    score += 5; // Yes or N/A
  }

  // Ensure score is within 0-100 bounds
  score = Math.max(0, Math.min(100, score));

  let preparednessCategory;
  if (score <= 20) {
    preparednessCategory = 'Critical Risk: Immediate Action Needed';
  } else if (score <= 40) {
    preparednessCategory = 'High Risk: Significant Gaps Exist';
  } else if (score <= 60) {
    preparednessCategory = 'Moderate Risk: Room for Improvement';
  } else if (score <= 80) {
    preparednessCategory = 'Low Risk: Generally Prepared';
  } else {
    preparednessCategory = 'Well Prepared: Excellent Readiness';
  }

  return {
    preparednessScore: score,
    preparednessCategory: preparednessCategory
  };
}
