function calculate(inputs) {
  let totalTireCost = 0;
  switch (inputs.tireServiceOption) {
    case 'new_winter_tires':
      totalTireCost = parseFloat(inputs.newWinterTireCost || 0);
      break;
    case 'all_season_rotation':
      totalTireCost = parseFloat(inputs.tireRotationCost || 0);
      break;
    case 'none':
    default:
      totalTireCost = 0;
      break;
  }

  const fluidServiceCost = parseFloat(inputs.fluidServiceCost || 0);
  const antifreezeFlushCost = parseFloat(inputs.antifreezeFlushCost || 0);
  const batteryServiceCost = parseFloat(inputs.batteryServiceCost || 0);
  const totalFluidBatteryCost = fluidServiceCost + antifreezeFlushCost + batteryServiceCost;

  const wiperBladeCost = parseFloat(inputs.wiperBladeCost || 0);
  const washerFluidGallons = parseFloat(inputs.washerFluidGallons || 0);
  const washerFluidCostPerGallon = parseFloat(inputs.washerFluidCostPerGallon || 0);
  const totalMiscellaneousCost = wiperBladeCost + (washerFluidGallons * washerFluidCostPerGallon);

  const totalEmergencyKitCost = parseFloat(inputs.emergencyKitCost || 0);

  const overallTotalCost = totalTireCost + totalFluidBatteryCost + totalMiscellaneousCost + totalEmergencyKitCost;

  return {
    totalTireCost: totalTireCost,
    totalFluidBatteryCost: totalFluidBatteryCost,
    totalEmergencyKitCost: totalEmergencyKitCost,
    totalMiscellaneousCost: totalMiscellaneousCost,
    overallTotalCost: overallTotalCost
  };
}
