function calculateScore(inputs) {
  const { 
    hoursWorkedPerWeek, 
    hoursCommutePerWeek, 
    hoursPersonalCarePerWeek, 
    hoursLeisureSocialPerWeek, 
    hoursFamilyResponsibilitiesPerWeek, 
    stressLevel, 
    energyLevel, 
    jobSatisfaction, 
    relationshipSatisfaction 
  } = inputs;

  // Input validation and default to 0 for missing/invalid inputs
  const safeInput = (val) => Math.max(0, parseFloat(val) || 0);

  const work = safeInput(hoursWorkedPerWeek);
  const commute = safeInput(hoursCommutePerWeek);
  const personalCare = safeInput(hoursPersonalCarePerWeek);
  const leisureSocial = safeInput(hoursLeisureSocialPerWeek);
  const family = safeInput(hoursFamilyResponsibilitiesPerWeek);

  const currentStress = Math.min(10, Math.max(1, parseInt(stressLevel) || 1));
  const currentEnergy = Math.min(10, Math.max(1, parseInt(energyLevel) || 1));
  const currentJobSat = Math.min(10, Math.max(1, parseInt(jobSatisfaction) || 1));
  const currentRelSat = Math.min(10, Math.max(1, parseInt(relationshipSatisfaction) || 1));

  const TOTAL_HOURS_WEEK = 168;

  // --- Step 1: Calculate Time Allocation Score (TAS) ---
  let tasScore = 100; // Start with a perfect score and apply penalties

  // Ideal ranges and penalties (max 100 points for TAS)
  // Work: Ideal 35-45. Penalize <35 and >45, heavily >55. 
  if (work < 35) tasScore -= (35 - work) * 1.5; // Penalty for under-working/unfulfillment 
  else if (work > 45 && work <= 55) tasScore -= (work - 45) * 2; // Moderate penalty for overworking
  else if (work > 55) tasScore -= (work - 55) * 4 + 20; // Heavy penalty for severe overworking (20 base + factor)

  // Commute: Ideal 0-5. Penalize >5, heavily >10. Max penalty 30 points.
  if (commute > 5 && commute <= 10) tasScore -= (commute - 5) * 3; 
  else if (commute > 10) tasScore -= Math.min(30, (commute - 10) * 5 + 15); // Cap commute penalty

  // Personal Care: Ideal 49-63 (7-9 hrs/day). Penalize <49.
  if (personalCare < 49) tasScore -= (49 - personalCare) * 1.5; // Penalty for insufficient rest/self-care
  else if (personalCare > 70) tasScore -= (personalCare - 70) * 0.5; // Slight penalty for excessive, could indicate issues

  // Leisure & Social: Ideal 15-30. Penalize <15, slight penalty for excessive >40.
  if (leisureSocial < 15) tasScore -= (15 - leisureSocial) * 2.5; // Penalty for insufficient recreation
  else if (leisureSocial > 40) tasScore -= (leisureSocial - 40) * 0.75; // Slight penalty for potentially avoiding other duties

  // Family & Domestic: Ideal 10-25. Penalize <10.
  if (family < 10) tasScore -= (10 - family) * 2; // Penalty for neglecting family/home

  // Total allocated hours check
  const totalAllocated = work + commute + personalCare + leisureSocial + family;
  if (totalAllocated > TOTAL_HOURS_WEEK + 5) { // Allow for a slight buffer
      tasScore -= (totalAllocated - (TOTAL_HOURS_WEEK + 5)) * 1.5; // Penalty for over-commitment
  } else if (totalAllocated < TOTAL_HOURS_WEEK - 20) { // Penalize significantly unallocated time
      tasScore -= (TOTAL_HOURS_WEEK - 20 - totalAllocated) * 0.5; 
  }

  tasScore = Math.max(0, Math.min(100, tasScore)); // Cap TAS between 0 and 100

  // --- Step 2: Calculate Subjective Well-being Score (SWS) ---
  // Stress is inverse: 10 stress = 1 point, 1 stress = 10 points
  const normalizedStress = 11 - currentStress; 

  const swsRaw = (normalizedStress + currentEnergy + currentJobSat + currentRelSat) / 4; // Average 1-10 scale
  const swsScore = Math.round(swsRaw * 10); // Convert to 0-100 scale

  // --- Step 3: Combine scores ---
  // Subjective well-being is often more impactful, so give it a higher weight
  const finalScore = Math.round((tasScore * 0.4) + (swsScore * 0.6));

  // --- Step 4: Generate Recommendation ---
  let recommendation = '';
  if (finalScore >= 85) {
    recommendation = 'Excellent Work-Life Balance Optimization! You exhibit a strong equilibrium across various life domains. Continue to monitor and adapt as life circumstances evolve, but you are currently in an optimal state.';
  } else if (finalScore >= 70) {
    recommendation = 'Healthy Balance with Room for Refinement. Your work-life balance is generally good, but there are areas you can optimize further. Review your time allocation and subjective metrics for potential minor adjustments to enhance well-being.';
  } else if (finalScore >= 50) {
    recommendation = 'Moderate Imbalance. Your score indicates a noticeable imbalance in certain areas. It is recommended to proactively identify which time categories or well-being metrics are pulling your score down and strategize tangible changes to improve your situation.';
  } else if (finalScore >= 30) {
    recommendation = 'Significant Imbalance. Your current work-life balance is likely causing stress and impacting your overall well-being. Urgent attention is advised. Focus on addressing the most critical areas of over-commitment or neglect, and consider seeking professional guidance.';
  } else {
    recommendation = 'Critical Imbalance Detected. This score points to a severe and potentially unsustainable work-life imbalance. Immediate action is required to prevent burnout and protect your health. Re-evaluate your priorities, delegate responsibilities, and consider consulting with a wellness professional or therapist.';
  }

  return { 
    optimizationScore: Math.max(0, Math.min(100, finalScore)), 
    recommendation 
  };
}
