const { surfaceType, weatherCondition, maintenanceFrequency, footTrafficLevel, lightingCondition, slopeIncline, spillDetectionRate, safetyCultureScore } = inputs;

// Step 1: Map input values to standardized risk scores (0-10 scale)
let surfaceRisk = 0;
switch (surfaceType) {
    case 0: surfaceRisk = 0; break; // Carpet/Rugs
    case 1: surfaceRisk = 2; break; // Concrete/Asphalt
    case 2: surfaceRisk = 3; break; // Hardwood/Laminate
    case 3: surfaceRisk = 4; break; // Ceramic/Stone Tile
    case 4: surfaceRisk = 5; break; // Loose Gravel/Dirt
    case 5: surfaceRisk = 6; break; // Metal Grating/Plating
    case 6: surfaceRisk = 10; break; // Ice/Snow
    default: surfaceRisk = 0;
}

let weatherRisk = 0;
switch (weatherCondition) {
    case 0: weatherRisk = 0; break; // Dry/Normal
    case 1: weatherRisk = 2; break; // Dusty/Powdery
    case 2: weatherRisk = 5; break; // Wet
    case 3: weatherRisk = 8; break; // Oily/Greasy
    case 4: weatherRisk = 10; break; // Icy/Snowy
    default: weatherRisk = 0;
}

let maintenanceRisk = 0;
switch (maintenanceFrequency) {
    case 0: maintenanceRisk = 1; break; // Daily/Continuously
    case 1: maintenanceRisk = 3; break; // Weekly
    case 2: maintenanceRisk = 6; break; // Monthly
    case 3: maintenanceRisk = 8; break; // Ad-hoc
    case 4: maintenanceRisk = 10; break; // Rarely/Never
    default: maintenanceRisk = 1;
}

let footTrafficRisk = 0;
switch (footTrafficLevel) {
    case 0: footTrafficRisk = 1; break; // Low
    case 1: footTrafficRisk = 3; break; // Moderate
    case 2: footTrafficRisk = 6; break; // High
    case 3: footTrafficRisk = 9; break; // Very High
    default: footTrafficRisk = 1;
}

let lightingRisk = 0;
switch (lightingCondition) {
    case 0: lightingRisk = 0; break; // Excellent
    case 1: lightingRisk = 1; break; // Good
    case 2: lightingRisk = 4; break; // Adequate
    case 3: lightingRisk = 8; break; // Poor
    default: lightingRisk = 0;
}

let slopeRisk = 0;
switch (slopeIncline) {
    case 0: slopeRisk = 0; break; // Flat/Level
    case 1: slopeRisk = 3; break; // Slight
    case 2: slopeRisk = 6; break; // Moderate
    case 3: slopeRisk = 10; break; // Steep
    default: slopeRisk = 0;
}

// Ensure spillDetectionRate is within 0-100 for calculation
const effectiveSpillDetectionRate = Math.max(0, Math.min(100, spillDetectionRate));
const spillRisk = (100 - effectiveSpillDetectionRate) / 10; // 0% detection = 10 risk, 100% detection = 0 risk

// Ensure safetyCultureScore is within 1-10 for calculation
const effectiveSafetyCultureScore = Math.max(1, Math.min(10, safetyCultureScore));
const safetyRisk = (10 - effectiveSafetyCultureScore); // 10 score = 0 risk, 1 score = 9 risk

// Step 2: Calculate weighted raw risk score
const weightedSum =
    (surfaceRisk * 20) +
    (weatherRisk * 20) +
    (maintenanceRisk * 15) +
    (footTrafficRisk * 15) +
    (lightingRisk * 10) +
    (slopeRisk * 10) +
    (spillRisk * 5) +
    (safetyRisk * 5);

const totalWeight = 100; // Sum of all weights

const rawRiskScore = weightedSum / totalWeight;

// Step 3: Normalize to a 0-100 scale, capping the maximum raw score based on theoretical maximums
const maxPossibleRawScore = 9.6; // (10*20 + 10*20 + 10*15 + 9*15 + 8*10 + 10*10 + 10*5 + 9*5) / 100 = 9.6
let riskScore = Math.round((rawRiskScore / maxPossibleRawScore) * 100);

// Ensure the score is within valid bounds (0-100)
riskScore = Math.max(0, Math.min(100, riskScore));

// Step 4: Determine risk category based on the normalized score
let riskCategory;
if (riskScore <= 20) {
    riskCategory = "Very Low";
} else if (riskScore <= 40) {
    riskCategory = "Low";
} else if (riskScore <= 60) {
    riskCategory = "Moderate";
} else if (riskScore <= 80) {
    riskCategory = "High";
} else {
    riskCategory = "Critical";
}

return {
    riskScore,
    riskCategory
};
