const numAthletes = Math.max(0, inputs.totalAthletesInLeague || 0);
const durationYears = Math.max(1, inputs.avgProgramDurationYears || 1);
const baselineRate = Math.max(0, inputs.baselineInjuryRatePer1000 || 0);
const reducedRate = Math.max(0, inputs.reducedInjuryRatePer1000 || 0);
const directMedical = Math.max(0, inputs.avgDirectMedicalCost || 0);
const indirectCost = Math.max(0, inputs.avgIndirectCostPerInjury || 0);
const retentionIncrease = Math.max(0, Math.min(100, inputs.projectedRetentionIncreasePct || 0)); // Cap at 100% of percentage points
const annualFee = Math.max(0, inputs.avgAnnualPlayerFee || 0);

// Costs
const totalProgramImplementationCost = inputs.programImplementationCost || 0;
const totalAnnualOperatingCost = (inputs.annualProgramOperatingCost || 0) * durationYears;
const totalProgramCost = totalProgramImplementationCost + totalAnnualOperatingCost;

// Injury Calculation
const annualBaselineInjuries = (numAthletes * baselineRate) / 1000;
const annualProgramInjuries = (numAthletes * reducedRate) / 1000;
const estimatedInjuriesPreventedAnnually = Math.max(0, annualBaselineInjuries - annualProgramInjuries);

// Financial Savings (over program duration)
const totalMedicalCostSavings = estimatedInjuriesPreventedAnnually * directMedical * durationYears;
const totalIndirectCostSavings = estimatedInjuriesPreventedAnnually * indirectCost * durationYears;

// Revenue Increase from Retention (annualized, then over duration)
const additionalRetainedAthletesAnnually = numAthletes * (retentionIncrease / 100);
const revenueIncreaseFromRetentionAnnually = additionalRetainedAthletesAnnually * annualFee;
const totalRevenueIncreaseFromRetention = revenueIncreaseFromRetentionAnnually * durationYears;

// Total Benefits
const totalFinancialBenefits = totalMedicalCostSavings + totalIndirectCostSavings + totalRevenueIncreaseFromRetention;

// Net Financial Benefit
const netFinancialBenefit = totalFinancialBenefits - totalProgramCost;

// ROI Percentage
let roiPercentage = 0;
if (totalProgramCost > 0) {
    roiPercentage = (netFinancialBenefit / totalProgramCost) * 100;
} else if (totalFinancialBenefits > 0) {
    roiPercentage = Infinity; // Infinite ROI if no cost and benefit
} else {
    roiPercentage = 0; // No cost, no benefit
}

// Health Impact (Qualitative)
let qualitativeHealthImpact = "No significant change expected. Program may not be effective or scope is too small.";
if (estimatedInjuriesPreventedAnnually > 0 && annualBaselineInjuries > 0) {
    const reductionRatio = estimatedInjuriesPreventedAnnually / annualBaselineInjuries;
    if (reductionRatio >= 0.5) {
        qualitativeHealthImpact = "Substantial improvement in athlete safety and well-being, significantly reducing injury burden.";
    } else if (reductionRatio >= 0.2) {
        qualitativeHealthImpact = "Moderate improvement in athlete safety and well-being, noticeably reducing injury rates.";
    } else {
        qualitativeHealthImpact = "Minor improvement in athlete safety. Further optimization may be needed.";
    }
} else if (annualBaselineInjuries === 0 && estimatedInjuriesPreventedAnnually === 0) {
    qualitativeHealthImpact = "Baseline injury rate is already zero or negligible; program may not be necessary for injury reduction.";
}

// Estimated Athlete Days Saved Annually (Assuming 7 days lost per injury on average for general youth sports, user can adjust mentally if their average is different)
const avgDaysLostPerInjury = 7; 
const estimatedAthleteDaysSavedAnnually = estimatedInjuriesPreventedAnnually * avgDaysLostPerInjury;


return {
    totalProgramCost: totalProgramCost,
    estimatedInjuriesPreventedAnnually: Math.round(estimatedInjuriesPreventedAnnually),
    totalMedicalCostSavings: totalMedicalCostSavings,
    totalIndirectCostSavings: totalIndirectCostSavings,
    totalRevenueIncreaseFromRetention: totalRevenueIncreaseFromRetention,
    totalFinancialBenefits: totalFinancialBenefits,
    netFinancialBenefit: netFinancialBenefit,
    roiPercentage: roiPercentage,
    estimatedAthleteDaysSavedAnnually: Math.round(estimatedAthleteDaysSavedAnnually),
    qualitativeHealthImpact: qualitativeHealthImpact
};
