function calculateImpact(inputs) {
  const { 
    totalParticipants, 
    avgWeeklyActivityHours, 
    socialIntegrationScore, 
    academicSupportEngagement, 
    communityServiceHours, 
    parentSatisfactionRate, 
    programDurationMonths 
  } = inputs;

  // Input validation and default to 0 for non-positive values to prevent errors
  const validatedTotalParticipants = Math.max(0, totalParticipants || 0);
  const validatedAvgWeeklyActivityHours = Math.max(0, avgWeeklyActivityHours || 0);
  const validatedSocialIntegrationScore = Math.max(0, Math.min(100, socialIntegrationScore || 0));
  const validatedAcademicSupportEngagement = Math.max(0, Math.min(100, academicSupportEngagement || 0));
  const validatedCommunityServiceHours = Math.max(0, communityServiceHours || 0);
  const validatedParentSatisfactionRate = Math.max(0, Math.min(100, parentSatisfactionRate || 0));
  const validatedProgramDurationMonths = Math.max(0, programDurationMonths || 0);

  // Step 1: Normalize inputs to a 0-100 scale based on optimal targets
  // Targets are set to represent what would be considered excellent performance for maximum score on that metric.

  // Participation: Optimal at 200 participants, caps impact contribution above that for direct influence per participant.
  const normParticipants = Math.min(100, (validatedTotalParticipants / 200) * 100);

  // Physical Activity: Optimal at 12 hours/week, aligned with public health recommendations for youth.
  const normActivityHours = Math.min(100, (validatedAvgWeeklyActivityHours / 12) * 100);

  // Social Integration: Directly mapped as it's already a percentage score.
  const normSocialIntegration = validatedSocialIntegrationScore;

  // Academic Engagement: Directly mapped as it's already a percentage score.
  const normAcademicEngagement = validatedAcademicSupportEngagement;

  // Community Service: Optimal at 20 hours/year/participant for significant impact.
  const normCommunityService = Math.min(100, (validatedCommunityServiceHours / 20) * 100);

  // Parent Satisfaction: Directly mapped as it's already a percentage score.
  const normParentSatisfaction = validatedParentSatisfactionRate;

  // Program Duration: Optimal at 60 months (5 years) for sustained, long-term impact.
  const normProgramDuration = Math.min(100, (validatedProgramDurationMonths / 60) * 100);

  // Step 2: Calculate specific impact scores
  // These scores combine normalized inputs with predefined weights for relevance.

  // Health & Physical Development Impact: Prioritizes activity hours, with program duration adding stability.
  const healthDevelopmentScore = (normActivityHours * 0.75) + (normProgramDuration * 0.25);

  // Social & Emotional Growth Impact: Balances direct social integration with parental perception.
  const socialEmotionalScore = (normSocialIntegration * 0.65) + (normParentSatisfaction * 0.35);

  // Academic & Cognitive Enhancement Impact: Primarily driven by academic support engagement, with program duration contributing to sustained learning environment.
  // Given only one academic input, its weight is high, but balanced slightly by overall program stability.
  const academicCognitiveScore = (normAcademicEngagement * 0.8) + (normProgramDuration * 0.2);

  // Community & Civic Engagement Impact: Heavily weighted on direct community service, with program duration supporting ongoing civic habits.
  const communityEngagementScore = (normCommunityService * 0.7) + (normProgramDuration * 0.3);

  // Step 3: Calculate the Overall Program Impact Score
  // This is a weighted average of the individual impact scores, reflecting holistic importance.
  // Weights for categories: Health (25%), Social (30%), Academic (20%), Community (25%)
  const overallImpactScore = (
    (healthDevelopmentScore * 0.25) +
    (socialEmotionalScore * 0.30) +
    (academicCognitiveScore * 0.20) +
    (communityEngagementScore * 0.25)
  );

  // Step 4: Ensure all output scores are capped at 100 and non-negative.
  return {
    overallImpactScore: Math.min(100, Math.max(0, overallImpactScore)),
    healthDevelopmentScore: Math.min(100, Math.max(0, healthDevelopmentScore)),
    socialEmotionalScore: Math.min(100, Math.max(0, socialEmotionalScore)),
    academicCognitiveScore: Math.min(100, Math.max(0, academicCognitiveScore)),
    communityEngagementScore: Math.min(100, Math.max(0, communityEngagementScore))
  };
}
